Template.leaderboard.helpers({
    leaderAvatar: function () {   
      return  Avatar.find();    
    },
    levelScores: function () {
      var stars = [];
      var score = 0;
      Levels.find().map(function(doc) {
        score += doc.score;
        stars.push(score);
      });
      return stars;

    },
    leaderScore: function () {
      var stars = {};

      Levels.find({level:1}).map(function(doc) {
        stars += doc.score;
      });
      Levels.find({level:2}).map(function(doc) {
        stars += doc.score;
      });
      Levels.find({level:3}).map(function(doc) {
        stars += doc.score;
      });
      Levels.find({level:4}).map(function(doc) {
        stars += doc.score;
      });
      Levels.find({level:5}).map(function(doc) {
        stars += doc.score;
      });

      return stars;
    }
  });
